import React, {useState, useEffect} from 'react';
import api from '../services/api';
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import CardClient from '../Componentes/cardClient';
import { Link } from 'react-router-dom';

function Client() {
    const location = useLocation();
    const idCliente = location.pathname.split("/").pop();
    const [client, setClient] = useState([]);
    const [cities, setCities] = useState([]);
 
    useEffect(() => {
        async function getClient() {
            const response = await api.get('/cliente/' + idCliente,)
            setClient(response.data)
            getAllCities(response.data.city)
        }
        getClient()
        async function getAllCities() {
            const response = await api.get('/cidade/',)
            setCities(response.data)
        }
        getAllCities()
    }, [])  

  async function handleSubmit(e) {
    e.preventDefault();
    const response = await api.post('/client/' + idCliente, {

    })
  }
  const htmlCitites = []
  for (let i = 0; i < cities; i++) {
    htmlCitites.push(<option value={cities[i].id}>{cities[i].name}</option>)  
    }
    console.log(htmlCitites)

    return (
        <div>
            <form onSubmit={handleSubmit} autoComplete="off">
                <fieldset>
                    <legend>Cliente</legend>
                     <label>Primeiro nome:</label><input class="campo_nome" type="text" id='first_name' value={client.first_name}></input><br></br>
                    <label>Sobrenome:</label><input class="campo_email" type="text" id='last_name' value={client.last_name}></input><br></br>
                    <label>Email:</label><input class="campo_email" type="text" id='email' value={client.email}></input><br></br>
                    <label>Genêro:</label><input class="campo_email" type="text" id='gender' value={client.gender}></input><br></br>
                    <label>Cidade:</label>
                    <select name="city" id="city">
                        {htmlCitites}
                    </select><br></br>
                    <label>Título:</label><input class="campo_email" type="text" id='title' value={client.title}></input><br></br>
                    <input class="btn_submit" type="submit" value="Enviar"></input>
                </fieldset>
            </form>  
        </div>
    )
}

export default Client;
