import React, {useState, useEffect} from 'react';
import api from '../services/api';
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import CardClient from '../Componentes/cardClient';
import { Link } from 'react-router-dom';

function Cient() {
    const location = useLocation();
    const idCliente = location.pathname.split("/").pop();

    const [client, setClient] = useState([]);
 
   useEffect(() => {
    async function getClient() {
    const response = await api.get('/cliente/' + idCliente,)
    setClient(response.data)
    }
    getClient()
  }, [])  
  console.log(client)

    return (
        <div>
            <form id="formulario" autocomplete="off">
                <fieldset>
                    <legend>Cliente</legend>
                    <label>Primeiro nome:</label><input class="campo_nome" type="text" id='first_name' value={client.data.first_name}></input><br></br>
                    <label>Sobrenome:</label><input class="campo_email" type="password" id='last_name' value={client.data.last_name}></input><br></br>
                    <label>Email:</label><input class="campo_email" type="password" id='email' value={client.data.email}></input><br></br>
                    <label>Genêro:</label><input class="campo_email" type="password" id='gender' value={client.data.gender}></input><br></br>
                    <label>Cidade:</label><input class="campo_email" type="password" id='city' value={client.data.city}></input><br></br>
                    <label>Título:</label><input class="campo_email" type="password" id='title' value={client.data.title}></input><br></br>
                    <input class="btn_submit" type="submit" value="Enviar"></input>
                </fieldset>
            </form>  
        </div>
    )
}

export default Cient;
