import React, {useState, useEffect} from 'react';
import api from '../services/api';
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import CardClient from '../Componentes/cardClient';
import { Link } from 'react-router-dom';
function useQuery() {
    const { search } = useLocation();
  
    return React.useMemo(() => new URLSearchParams(search), [search]);
  }
function City() {
    const location = useLocation();
    const idCidade = location.pathname.split("/").pop();
    const [city, setCity] = useState([]);
    const [allClientsByCity, setAllClientsByCity] = useState([]);
 
   useEffect(() => {
    async function getCity() {
      const response = await api.get('/cidade/' + idCidade,)
      setCity(response.data)
    }
    getCity()
    async function getAllUsersByCity() {
      let query = useQuery();
      const page = query.get("page") ? query.get("page") : 1;
      const response = await api.get('/cliente/cidade/' + idCidade + '?page=' + page,)
      setAllClientsByCity(response.data)
    }
    getAllUsersByCity()
  }, [])  

  console.log()
    return (
        <div id="Home">
            <section className='card'>
                 <ul>
                     {allClientsByCity.map(data => {
                        return <CardClient data={data}/>
                        })}
                 </ul>
            </section>
            <div>
                Paginação
                <div>
                    <ul>
                      <Link to="/cidade/1?page=1">
                        <li>1</li>
                        </Link>
                        <Link to="/cidade/1?page=2">
                        <li >2</li>
                        </Link>
                        <li>3</li>
                    </ul>
                </div>
            </div>
        </div>

    )
}

export default City;
