import React, {useState, useEffect} from 'react';
import api from '../services/api';
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import CardClient from '../Componentes/cardClient';
import { Link } from 'react-router-dom';

function Cient() {
    const location = useLocation();
    const idCliente = location.pathname.split("/").pop();

    const [client, setClient] = useState([]);
 
   useEffect(() => {
    async function getClient() {
    const response = await api.get('/cliente/' + idCliente,)
    setClient(response.data)
    }
    getClient()
  }, [])  
    console.log(client)
    return (
        <div>
            <form id="formulario" autocomplete="off">
                <fieldset>
                    <legend>Cliente</legend>
                    <label>Nome:</label><input class="campo_nome" type="text"></input><br></br>
                    <label>Email:</label><input class="campo_email" type="password"></input><br></br>
                    <input class="btn_submit" type="submit" value="Enviar"></input>
                </fieldset>
            </form>  
        </div>
    )
}

export default Cient;
